
public class ConcatStringInt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 10;
		String a = "hello";
		String s = a+String.valueOf(num);
		System.out.println(s);
		
		float f = 3.14F;
		String s1 = a+String.valueOf(f);
		System.out.println(s1);
	}

}
