
public class StringReverseWord {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String name = "Jyotsana Pandey";
			     String arr[] = name.split(" ");
			     for(int i=arr.length-1;i>=0;i--)
			      {
			        System.out.print(arr[i]+" ");
			      }
	}

}
